INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('1', 'Viva la Vida', 'Coldplay', 'Viva la Vida', NULL, 'E:\\MusicFile\\Viva la Vida.mp3', '0', '歌曲描写了一个国王失去的王国的故事，其中心思想是“反独裁”。[8]  歌曲主题是“记住你生活中什么是重要的”。[8] \r\n克里斯・马汀负责了歌曲的钢琴演奏，音效合成器和背景和声由布莱恩・伊诺负责，大卫・罗西负责歌曲的整体连接。[8] \r\n歌曲描写了一个国王失去的王国的故事，其中心思想是“反独裁”。[8]  歌曲主题是“记住你生活中什么是重要的”。[8] \r\n克里斯・马汀负责了歌曲的钢琴演奏，音效合成器和背景和声由布莱恩・伊诺负责，大卫・罗西负责歌曲的整体连接。[8] \r\n歌曲描写了一个国王失去的王国的故事，其中心思想是“反独裁”。');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('2', 'Hero', 'Mariah Carey', 'The Ballads', NULL, 'E:\\MusicFile\\Hero.mp3 \r\n\r\n', '0', '《Hero》是由美国流行女歌手玛丽亚・凯莉演唱的一首歌曲。歌曲由玛利亚・凯莉与华特・亚凡瑟夫谱写，作为第二支单曲收录在玛利亚・凯莉第三张录音室专辑《Music Box》中，于1993年10月19日由哥伦比亚唱片公司发行。美国NBA球星迈克尔・乔丹将《Hero》作为了自己退役时的主题曲');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('4', 'A Sky Full of Stars', 'Coldplay', 'Ghost stories', NULL, 'E:MusicFile\\A Sky Full of Stars.mp3', '0', '《A Sky Full of Stars》是英国另类摇滚乐队酷玩乐队演唱的一首流行歌曲，由乐队及瑞典音乐制作人Avicii进行词曲创作，Avicii、乐队及保罗・埃普沃思等人编曲制作，被Parlophone唱片公司作为乐队六专《Ghost Stories》的第三支单曲发行于2014年5月2日');
