INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('101', 'Blank Space', 'Taylor Swift', '1989', NULL, 'E:MusicFile\\Blank Space.flac', '0', '《Blank Space》是美国女歌手泰勒・斯威夫特演唱的一首流行歌曲，歌词、简谱均由泰勒・斯威夫特、马克斯・马丁和希尔贝克合作编写，后两位同时负责音乐制作。该歌曲被收录在泰勒・斯威夫特于2014年10月27日发行的第五张录音室专辑《1989》');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('102', 'Breathe', 'Taylor Swift', 'Fearless', NULL, 'E:MusicFile\\Breathe.mp3', '0', '《Breathe》是Taylor Swift 在专辑《Fearless》中的一首与Colbie Caillat合唱的美国抒情乡村风格的歌曲。');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('105', 'Grenade', 'Bruno Mars', 'Grenade', NULL, 'E:MusicFile\\Grenade.mp3', '0', 'Grenade》是美国歌手布鲁诺・马尔斯演唱的一首歌曲，由布鲁诺・马尔斯、菲利普・劳伦斯、阿里・莱文、布洛迪・布朗、克劳德・凯利和安德鲁・怀亚特共同创作，作为第二支单曲收录在布鲁诺・马尔斯首张录音室专辑《Doo-Wops & Hooligans》中，于2010年9月8日由Elektra公司发行。');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('106', 'Blow Me', 'P!nk', 'The Truth About Love', NULL, 'E:MusicFile\\Blow Me.flac', '0', '《Blow Me (One Last Kiss)》是美国女歌手P!nk演唱的一首歌曲，收录在P!nk的第六张录音室专辑《The Truth About Love》中，作为所在专辑的首发单曲，于2012年7月3日通过RCA唱片公司发行，由P!nk和格莱格・科斯汀填词，格莱格・科斯汀谱曲。');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('107', 'Just the Way You Are', 'Antheqm Lights', 'Anthem Lights:Ascontic Session', NULL, 'E:MusicFile\\Just the Way You Are.mp3', '0', '《Just the Way You Are》是由美国流行男歌手布鲁诺・马尔斯演唱的一首歌曲，由The Smeezingtons（布鲁诺・马尔斯、菲利普・劳伦斯、阿里・莱文三人创作团队）填词、The Smeezingtons、哈利勒・沃尔顿和Needlz谱曲，收录于布鲁诺・马尔斯首张录音室专辑《Doo-Wops & Hooligans》中，于2010年7月20日通过Elektra唱片和大西洋唱片发布。');
INSERT INTO `music`.`musicmenu` (`music_id`, `music_name`, `music_singer`, `music_special`, `music_time`, `music_url`, `music_listened_count`, `music_introduce`) VALUES ('115', '斑马，斑马', '宋冬野', '安河桥北', NULL, 'E:MusicFile\\斑马，斑马.mp3', '0', '《斑马，斑马》是宋冬野演唱的歌曲，由宋冬野作词作曲，收录在宋冬野2013年8月27日发行的专辑《安和桥北》中。 \r\n2014年8月15日，歌曲《斑马，斑马》官方MV正式发布。');
